import React, { useEffect, useState } from "react";
import "./App.css";

/** ========== CONFIG ========== */
const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

/** ========== API HELPERS (no mesmo arquivo p/ simplificar) ========== */
async function fetchStats() {
  const res = await fetch(`${API_URL}/stats`);
  const json = await res.json().catch(() => ({ success: false, error: "JSON inválido" }));
  if (!res.ok || json.success === false) throw new Error(json?.error || `HTTP ${res.status}`);
  return json.data; // { dish_stats:[{name,capacity,confirmed,remaining}], ... }
}

async function createRsvp(payload) {
  const res = await fetch(`${API_URL}/rsvps`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(payload),
  });
  const json = await res.json().catch(() => ({ success: false, error: "JSON inválido" }));
  if (!res.ok || json.success === false) throw new Error(json?.error || `HTTP ${res.status}`);
  return json.data; // RSVP criado
}

/** ========== ESTILINHO SIMPLES (para inputs) ========== */
const inputStyle = {
  width: "100%",
  padding: "10px 12px",
  borderRadius: 10,
  background: "#0f172a",
  border: "1px solid #334155",
  color: "white",
};

/** ========== APP ========== */
export default function App() {
  const [stats, setStats] = useState(null);
  const [sending, setSending] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(null); // { name, dish }

  // carrega stats e faz polling simples
  useEffect(() => {
    let alive = true;

    const load = async () => {
      try {
        const data = await fetchStats();
        if (alive) setStats(data);
      } catch (e) {
        console.error(e);
      }
    };

    load();
    const id = setInterval(load, 10000); // atualiza a cada 10s
    return () => {
      alive = false;
      clearInterval(id);
    };
  }, []);

  async function handleSubmit(e) {
    e.preventDefault();
    setError("");
    setSending(true);

    const form = e.target;

    // monta o payload com os names do <form>
    const payload = {
      name: form.name.value.trim(),
      email: form.email.value.trim(),
      phone: form.phone.value.trim(),
      guests: Number(form.guests.value || 1),
      selected_dish: form.selectedDish.value, // NOME do prato igual ao backend
      payment_type: form.paymentType.value || "individual",
      notes: form.notes.value || "",
      payment_proof: "",
    };

    // validação básica
    if (!payload.name || !payload.email || !payload.phone || !payload.selected_dish) {
      setSending(false);
      return setError("Preencha nome, e-mail, telefone e selecione um prato.");
    }

    try {
      console.log("POST ->", `${API_URL}/rsvps`, payload);
      await createRsvp(payload);          // só continua se SALVOU no backend
      const data = await fetchStats();    // puxa stats atualizados
      setStats(data);                     // atualiza os cards
      setSuccess({ name: payload.name, dish: payload.selected_dish }); // mostra sucesso
      form.reset();
    } catch (err) {
      console.error(err);
      setError(err.message || "Falha ao enviar. Tente novamente.");
    } finally {
      setSending(false);
    }
  }

  if (!stats) {
    return <div style={{ padding: 24, color: "white" }}>Carregando…</div>;
  }

  return (
    <div style={{ maxWidth: 1100, margin: "0 auto", padding: 24, color: "white" }}>
      <h2 style={{ textAlign: "center", fontWeight: 800, fontSize: 28, marginBottom: 24 }}>
        {"> SUPRIMENTOS DISPONÍVEIS _"}
      </h2>

      {/* CARDS DE PRATO */}
      <div
        style={{
          display: "grid",
          gap: 16,
          gridTemplateColumns: "repeat(auto-fill, minmax(280px, 1fr))",
        }}
      >
        {stats.dish_stats.map((d) => {
          const pct = d.capacity ? (d.confirmed / d.capacity) * 100 : 0;
          const status = d.remaining > 0 ? "DISPONÍVEL" : "LOTADO";
          return (
            <div
              key={d.name}
              style={{
                padding: 16,
                borderRadius: 16,
                background: "rgba(30,41,59,.6)",
                border: "1px solid #334155",
              }}
            >
              <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <b>{d.name}</b>
                <span
                  style={{
                    fontSize: 12,
                    padding: "2px 8px",
                    borderRadius: 6,
                    background: d.remaining > 0 ? "rgba(34,197,94,.2)" : "rgba(239,68,68,.2)",
                    color: d.remaining > 0 ? "#86efac" : "#fca5a5",
                  }}
                >
                  {status}
                </span>
              </div>
              <div style={{ marginTop: 8, opacity: 0.8, fontSize: 14 }}>
                Vagas: {d.confirmed}/{d.capacity}
              </div>
              <div style={{ marginTop: 8, height: 8, background: "#334155", borderRadius: 6 }}>
                <div style={{ height: 8, width: `${pct}%`, background: "#22c55e", borderRadius: 6 }} />
              </div>
            </div>
          );
        })}
      </div>

      <h2 style={{ fontWeight: 800, fontSize: 24, marginTop: 32 }}>Registro de Viajante</h2>

      {/* TELA DE SUCESSO */}
      {success && (
        <div
          style={{
            marginTop: 16,
            padding: 16,
            borderRadius: 16,
            background: "rgba(30,41,59,.6)",
            border: "1px solid #334155",
          }}
        >
          <h3 style={{ fontWeight: 800, fontSize: 20, marginBottom: 8 }}>🎉 Portal ativado com sucesso!</h3>
          <p>
            Obrigado, <b>{success.name}</b>!
          </p>
          <p>
            Prato selecionado: <b>{success.dish}</b>
          </p>
          <p style={{ opacity: 0.8, marginTop: 8 }}>Envie o comprovante para confirmar sua presença.</p>
          <button
            onClick={() => setSuccess(null)}
            style={{ marginTop: 12, padding: "8px 12px", borderRadius: 8, background: "#334155", color: "white" }}
          >
            Fazer outro cadastro
          </button>
        </div>
      )}

      {/* FORMULÁRIO */}
      {!success && (
        <form
          onSubmit={handleSubmit}
          style={{
            marginTop: 16,
            padding: 16,
            borderRadius: 16,
            background: "rgba(30,41,59,.6)",
            border: "1px solid #334155",
          }}
        >
          <div style={{ display: "grid", gap: 12 }}>
            <div>
              <label>Nome completo *</label>
              <input name="name" style={inputStyle} />
            </div>

            <div>
              <label>E-mail *</label>
              <input name="email" type="email" style={inputStyle} />
            </div>

            <div>
              <label>Telefone *</label>
              <input name="phone" style={inputStyle} />
            </div>

            <div>
              <label>Convidados</label>
              <input name="guests" type="number" min="1" defaultValue="1" style={inputStyle} />
            </div>

            <div>
              <label>Prato (obrigatório)</label>
              <select name="selectedDish" style={inputStyle}>
                <option value="">-- selecione --</option>
                {stats.dish_stats.map((d) => (
                  <option key={d.name} value={d.name} disabled={d.remaining <= 0}>
                    {d.name} {d.remaining <= 0 ? "(LOTADO)" : ""}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label>Tipo de pagamento</label>
              <select name="paymentType" style={inputStyle} defaultValue="individual">
                <option value="individual">Individual</option>
                <option value="grupo">Grupo</option>
              </select>
            </div>

            <div>
              <label>Observações</label>
              <input name="notes" style={inputStyle} />
            </div>
          </div>

          {error && <p style={{ color: "#fca5a5", marginTop: 8 }}>{error}</p>}

          <button
            type="submit"
            disabled={sending}
            style={{
              marginTop: 12,
              padding: "10px 14px",
              borderRadius: 10,
              background: "#22c55e",
              color: "black",
              fontWeight: 700,
              opacity: sending ? 0.6 : 1,
            }}
          >
            {sending ? "Enviando..." : "Confirmar presença"}
          </button>
        </form>
      )}

      <p style={{ marginTop: 16, opacity: 0.6, fontSize: 12 }}>API_URL = {API_URL}</p>
    </div>
  );
}
